mapper.select:
select o.*
from basic_order o
join basic_user u on u.id=o.user_id
join basic_user_house uh on uh.user_id=u.id and uh.owner_status=1
join basic_house house on house.id=uh.house_id
join basic_product product on product.id=o.product_id


			product.type=#{type}


			o.date_create>#{createTimeStart}


			o.date_create<#{createTimeEnd}


			o.order_status=#{orderStatus}


			u.real_name=#{userRealName}


			u.mobile=#{userMobile}


			house.area_id=#{areaId}


			house.building=#{houseBuilding}


			house.unit=#{houseUnit}


			house.number=#{houseNumber}


	order by o.date_create desc
	LIMIT #{page} , #{psize};

select count(*)
from basic_order o
join basic_user u on u.id=o.user_id
join basic_user_house uh on uh.user_id=u.id and uh.owner_status=2
join basic_house house on house.id=uh.house_id
join basic_product product on product.id=o.product_id


			product.type=#{type}


			o.date_create>#{createTimeStart}


			o.date_create<#{createTimeEnd}


			o.order_status=#{orderStatus}


			u.real_name=#{userRealName}


			u.mobile=#{userMobile}


			house.area_id=#{areaId}


			house.building=#{houseBuilding}


			house.unit=#{houseUnit}


			house.number=#{houseNumber}
