mapper.select:
select u.id as userId, u.mobile, u.date_create as dateCreate, u.real_name as realName, u.email, u.gender, u.birthday,
uh.house_id as houseId, a.name as areaName, CONCAT(h.building,'栋',h.unit,'单元',h.number,'号') as houseText,
uh.owner_status as ownerStatus, uh.owner_type as ownerType, uh.owner_reason as ownerReason, su.real_name as ownerUpdateUname, uh.owner_update_time as ownerUpdateTime
from basic_user_house uh join basic_user u on u.id=uh.user_id join basic_house h on h.id=uh.house_id join basic_area a on a.id=h.area_id left join sys_user su on su.id=uh.owner_update_uid
u.rea_name=#{realName}
u.mobile=#{mobile}
u.date_create>#{dateCreateStart}
u.date_create<#{dateCreateEnd}
uh.owner_status=#{ownerStatus}
order by uh.owner_update_time desc LIMIT #{page} , #{psize};
